import React, { useState } from 'react';
import { Menu, X } from 'lucide-react'; // make sure lucide-react is installed

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav className="bg-blue-700 text-white fixed w-full top-0 z-50 shadow">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-10">
        <div className="flex justify-between items-center h-16">
          
          {/* Logo + Title */}
          <div className="flex items-center space-x-3">
            <img src="/signbridge-logo.png" alt="Logo" className="h-8 w-8" />
            <span className="font-semibold text-lg tracking-wide">SignBridge</span>
          </div>

          {/* Desktop Links */}
          <div className="hidden md:flex items-center space-x-6">
            <a href="/" className="hover:text-blue-200 transition">Home</a>
            <a href="/dashboard" className="hover:text-blue-200 transition">Dashboard</a>
            <a href="/about" className="hover:text-blue-200 transition">About</a>
            <a href="/contact" className="hover:text-blue-200 transition">Contact</a>
          </div>

          {/* Hamburger Icon */}
          <div className="md:hidden flex items-center">
            <button onClick={toggleMenu} className="focus:outline-none">
              {isOpen ? <X size={26} /> : <Menu size={26} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Links */}
      {isOpen && (
        <div className="md:hidden bg-blue-600 px-4 pt-4 pb-6 space-y-4 transition-all duration-300">
          <a href="/" className="block hover:text-blue-200">Home</a>
          <a href="/dashboard" className="block hover:text-blue-200">Dashboard</a>
          <a href="/about" className="block hover:text-blue-200">About</a>
          <a href="/contact" className="block hover:text-blue-200">Contact</a>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
