import React from 'react';
// import logo from './logo.png'; // use real logo later

function Navbar() {
  return (
    <nav className="bg-blue-600 text-white flex flex-col md:flex-row items-center justify-between px-4 py-3 shadow-md rounded-b-lg">

      <div className="flex items-center space-x-2">
        {/* Logo placeholder */}
        {/* <img src={logo} alt="SignBridge Logo" className="h-8 w-auto" /> */}
        <span className="font-bold text-xl tracking-wide">SignBridge</span>

      </div>
      <div className="flex space-x-4 mt-2 md:mt-0">
        <a href="#" className="hover:bg-blue-700 px-3 py-1 rounded">Dashboard</a>
        <a href="#" className="hover:bg-blue-700 px-3 py-1 rounded">Upload Lessons</a>
        <a href="#" className="hover:bg-blue-700 px-3 py-1 rounded">Profile</a>
      </div>

    </nav>
  );
}

export default Navbar;
