import React from 'react';

function About() {
  return (
    <div className="max-w-3xl mx-auto py-8 px-4">
      <h1 className="text-3xl font-bold text-blue-600 mb-4">About SignBridge</h1>
      <p className="text-gray-700 mb-4">
        SignBridge is an inclusive platform designed to help people learn and teach sign language easily.
        Our goal is to break communication barriers and empower the deaf and hearing communities alike.
      </p>
      <p className="text-gray-700 mb-4">
        Instructors can upload video lessons and manage their students, while learners can access structured courses anytime, anywhere.
      </p>
      <p className="text-gray-700">
        Together, we’re building a bridge of understanding — one sign at a time.
      </p>
    </div>
  );
}

export default About;
