import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';

// Temporary pages (we'll build real ones next)
function Dashboard() {
  return <h1 className="text-3xl font-bold text-blue-600">Instructor Dashboard</h1>;
}

function UploadLessons() {
  return <h1 className="text-3xl font-bold text-blue-600">Upload Lessons Page</h1>;
}

function EditProfile() {
  return <h1 className="text-3xl font-bold text-blue-600">Edit Profile Page</h1>;
}

function MyStudents() {
  return <h1 className="text-3xl font-bold text-blue-600">My Students Page</h1>;
}

function InterestForm() {
  return <h1 className="text-3xl font-bold text-blue-600">Instructor Interest Form Page</h1>;
}

function App() {
  return (
    <Router>
      <div className="bg-white min-h-screen">
        <Navbar />
        <main className="max-w-4xl mx-auto p-4">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/upload" element={<UploadLessons />} />
            <Route path="/profile" element={<EditProfile />} />
            <Route path="/students" element={<MyStudents />} />
            <Route path="/interest-form" element={<InterestForm />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
